// import logo from './logo.svg';
import './App.css';

let fakeServerData = {
  user: {
    name: "Steve",
    playlists: [
      {
        name: "Love songs",
        songs: [
          { name: "Beat it", duration: 1234 },
          { name: "I like you", duration: 1234 }
        ]
      },
      {
        name: "Hit songs",
        songs: [
          { name: "Last Christmas", duration: 1234 },
          { name: "All I Want For Christmas Is You", duration: 1234 }
        ]
      },
      {
        name: "Seasons songs",
        songs: [
          { name: "Fall", duration: 1234 },
          { name: "Summer", duration: 1234 }
        ]
      }
    ]
  }
}
